require 'test/unit'
require 'byebug'

def minimum_moves(numbers)
  sorted = numbers.sort
  median = sorted[sorted.length / 2]
  moves = 0

  sorted.each do |number|
    moves += (number - median).abs
  end

  moves
end

def minimum_moves_quickselect(numbers)
  median = quickselect(numbers, numbers.length / 2)

  numbers.reduce(0) do |acc, number|
    acc += (number - median).abs
  end
end

def quickselect(list, kth)
  smaller, equal, larger = partition(list)

  return quickselect(smaller, kth) if kth < smaller.length
  return equal[0] if kth < smaller.length + equal.length

  quickselect(larger, kth - smaller.length - equal.length)
end

def partition(list)
  pivot = list[list.length / 2]

  smaller = list.select { |x| x < pivot }
  equal = list.select { |x| x == pivot }
  larger = list.select { |x| x > pivot }

  [smaller, equal, larger]
end

class MinimumMovesTest < Test::Unit::TestCase
  def test_mininum_moves
    assert_equal(2, minimum_moves([1, 2, 3]))
    assert_equal(16, minimum_moves([1, 10, 2, 9]))

